import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@/core/util/services/api.service';
import { required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import useServerValidation from '@/core/util/composable/useServerValidation';
export default {
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/pay-grades');
    const {
      createUniqueValidator
    } = useServerValidation(http);
    const payGradeUniqueValidation = createUniqueValidator('PayGrade', 'name');
    return {
      http,
      payGradeUniqueValidation
    };
  },
  data() {
    return {
      isLoading: false,
      grade: {
        id: '',
        name: ''
      },
      rules: {
        name: [required, this.payGradeUniqueValidation, shouldNotExceedCharLength(50)]
      },
      errors: []
    };
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.create({
        name: this.grade.name
      }).then(response => {
        const {
          data
        } = response.data;
        this.$toast.saveSuccess();
        navigate('/admin/payGrade/{id}', {
          id: data.id
        });
      });
    },
    onCancel() {
      navigate('/admin/viewPayGrades');
    }
  }
};
