import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@ohrm/core/util/services/api.service';
import { required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import useServerValidation from '@/core/util/composable/useServerValidation';
export default {
  props: {
    employmentStatusId: {
      type: Number,
      required: true
    }
  },
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/employment-statuses');
    const {
      createUniqueValidator
    } = useServerValidation(http);
    const employmentStatusValidation = createUniqueValidator('EmploymentStatus', 'name', {
      entityId: props.employmentStatusId
    });
    return {
      http,
      employmentStatusValidation
    };
  },
  data() {
    return {
      isLoading: false,
      employmentStatus: {
        id: '',
        name: ''
      },
      rules: {
        name: [required, this.employmentStatusValidation, shouldNotExceedCharLength(50)]
      }
    };
  },
  created() {
    this.isLoading = true;
    this.http.get(this.employmentStatusId).then(response => {
      const {
        data
      } = response.data;
      this.employmentStatus.id = data.id;
      this.employmentStatus.name = data.name;
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.update(this.employmentStatusId, {
        name: this.employmentStatus.name
      }).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    onCancel() {
      navigate('/admin/employmentStatus');
    }
  }
};
