import { APIService } from '@/core/util/services/api.service';
import { required, validEmailFormat, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import { OxdDialog } from '@ohrm/oxd';
import useServerValidation from '@/core/util/composable/useServerValidation';
const subscriberModel = {
  name: '',
  email: ''
};
export default {
  name: 'SaveSubscriber',
  components: {
    'oxd-dialog': OxdDialog
  },
  props: {
    data: {
      type: Object,
      default: () => ({})
    }
  },
  emits: ['close'],
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, `/api/v2/admin/email-subscriptions/${props.data.subscriptionId}/subscribers`);
    const {
      createUniqueValidator
    } = useServerValidation(http);
    const subscriberUniqueValidation = createUniqueValidator('EmailSubscriber', 'email', {
      matchByField: 'emailNotification',
      matchByValue: props.data.subscriptionId
    });
    return {
      http,
      subscriberUniqueValidation
    };
  },
  data() {
    return {
      isLoading: false,
      subscriber: {
        ...subscriberModel
      },
      rules: {
        name: [required, shouldNotExceedCharLength(100)],
        email: [required, validEmailFormat, this.subscriberUniqueValidation, shouldNotExceedCharLength(100)]
      }
    };
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.create({
        ...this.subscriber
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    onCancel() {
      this.subscriber = {
        ...subscriberModel
      };
      this.$emit('close', true);
    }
  }
};
