import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@/core/util/services/api.service';
import { required, shouldNotExceedCharLength, validFileTypes, maxFileSize } from '@ohrm/core/util/validation/rules';
import FileUploadInput from '@/core/components/inputs/FileUploadInput';
import useServerValidation from '@/core/util/composable/useServerValidation';
const initialJobTitle = {
  title: '',
  description: '',
  oldSpecification: '',
  newSpecification: null,
  method: 'keepCurrent',
  note: ''
};
export default {
  components: {
    'file-upload-input': FileUploadInput
  },
  props: {
    jobTitleId: {
      type: String,
      required: true
    },
    allowedFileTypes: {
      type: Array,
      required: true
    },
    maxFileSize: {
      type: Number,
      required: true
    }
  },
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/job-titles');
    const {
      createUniqueValidator
    } = useServerValidation(http);
    const jobTitleUniqueValidation = createUniqueValidator('JobTitle', 'jobTitleName', {
      entityId: props.jobTitleId,
      matchByField: 'isDeleted',
      matchByValue: 'false'
    });
    return {
      http,
      jobTitleUniqueValidation
    };
  },
  data() {
    return {
      isLoading: false,
      jobTitle: {
        ...initialJobTitle
      },
      rules: {
        title: [required, this.jobTitleUniqueValidation, shouldNotExceedCharLength(100)],
        description: [shouldNotExceedCharLength(400)],
        specification: [v => {
          if (this.jobTitle.method == 'replaceCurrent') {
            return required(v);
          } else {
            return true;
          }
        }, validFileTypes(this.allowedFileTypes), maxFileSize(this.maxFileSize)],
        note: [shouldNotExceedCharLength(400)]
      }
    };
  },
  computed: {
    formattedFileSize() {
      return Math.round(this.maxFileSize / (1024 * 1024) * 100) / 100;
    }
  },
  created() {
    this.isLoading = true;
    this.http.get(this.jobTitleId).then(response => {
      const {
        data
      } = response.data;
      this.jobTitle.title = data.title;
      this.jobTitle.description = data.description;
      this.jobTitle.note = data.note;
      this.jobTitle.oldSpecification = data.jobSpecification?.id ? data.jobSpecification : null;
      this.jobTitle.newSpecification = null;
      this.jobTitle.method = 'keepCurrent';
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onCancel() {
      navigate('/admin/viewJobTitleList');
    },
    onSave() {
      this.isLoading = true;
      this.http.update(this.jobTitleId, {
        title: this.jobTitle.title,
        description: this.jobTitle.description,
        note: this.jobTitle.note,
        currentJobSpecification: this.jobTitle.oldSpecification ? this.jobTitle.method : undefined,
        specification: this.jobTitle.newSpecification ? this.jobTitle.newSpecification : undefined
      }).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => {
        this.onCancel();
      });
    }
  }
};
