import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@ohrm/core/util/services/api.service';
import { required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import useServerValidation from '@/core/util/composable/useServerValidation';
export default {
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/pim/termination-reasons');
    const {
      createUniqueValidator
    } = useServerValidation(http);
    const terminationReasonUniqueValidation = createUniqueValidator('TerminationReason', 'name');
    return {
      http,
      terminationReasonUniqueValidation
    };
  },
  data() {
    return {
      isLoading: false,
      termination: {
        id: '',
        name: ''
      },
      rules: {
        name: [required, this.terminationReasonUniqueValidation, shouldNotExceedCharLength(100)]
      }
    };
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.create({
        name: this.termination.name
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    onCancel() {
      navigate('/pim/viewTerminationReasons');
    }
  }
};
