import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@/core/util/services/api.service';
import { required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import useServerValidation from '@/core/util/composable/useServerValidation';
export default {
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/job-categories');
    const {
      createUniqueValidator
    } = useServerValidation(http);
    const jobCategoryUniqueValidation = createUniqueValidator('JobCategory', 'name');
    return {
      http,
      jobCategoryUniqueValidation
    };
  },
  data() {
    return {
      isLoading: false,
      category: {
        id: '',
        name: ''
      },
      rules: {
        name: [required, shouldNotExceedCharLength(50), this.jobCategoryUniqueValidation]
      },
      errors: []
    };
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.create({
        name: this.category.name
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    onCancel() {
      navigate('/admin/jobCategory');
    }
  }
};
