import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@ohrm/core/util/services/api.service';
import { required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import useServerValidation from '@/core/util/composable/useServerValidation';
const skillModel = {
  id: '',
  name: '',
  description: ''
};
export default {
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/skills');
    const {
      createUniqueValidator
    } = useServerValidation(http);
    const skillUniqueValidation = createUniqueValidator('Skill', 'name');
    return {
      http,
      skillUniqueValidation
    };
  },
  data() {
    return {
      isLoading: false,
      skill: {
        ...skillModel
      },
      rules: {
        name: [required, shouldNotExceedCharLength(120), this.skillUniqueValidation],
        description: [shouldNotExceedCharLength(400)]
      },
      errors: []
    };
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.create({
        name: this.skill.name,
        description: this.skill.description
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        this.skill.name = '';
        this.skill.description = '';
        this.onCancel();
      });
    },
    onCancel() {
      navigate('/admin/viewSkills');
    }
  }
};
