import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@ohrm/core/util/services/api.service';
import { required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import EntitlementSituationalModal from '@/orangehrmLeavePlugin/components/EntitlementSituationalModal';
import { OxdLabel } from '@ohrm/oxd';
import useServerValidation from '@/core/util/composable/useServerValidation';
const leaveTypeModel = {
  id: '',
  name: '',
  situational: ''
};
export default {
  components: {
    'oxd-label': OxdLabel,
    'entitlement-situational-modal': EntitlementSituationalModal
  },
  props: {
    leaveTypeId: {
      type: Number,
      required: true
    }
  },
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/leave/leave-types');
    const {
      createUniqueValidator
    } = useServerValidation(http);
    const leaveTypeUniqueValidation = createUniqueValidator('LeaveType', 'name', {
      entityId: props.leaveTypeId
    });
    return {
      http,
      leaveTypeUniqueValidation
    };
  },
  data() {
    return {
      showModal: false,
      isLoading: false,
      leaveType: {
        ...leaveTypeModel
      },
      rules: {
        name: [required, this.leaveTypeUniqueValidation, shouldNotExceedCharLength(50)]
      }
    };
  },
  created() {
    this.isLoading = true;
    this.http.get(this.leaveTypeId).then(response => {
      const {
        data
      } = response.data;
      this.leaveType.id = data.id;
      this.leaveType.name = data.name;
      this.leaveType.situational = data.situational;
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.update(this.leaveTypeId, {
        name: this.leaveType.name,
        situational: this.leaveType.situational
      }).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    onCancel() {
      navigate('/leave/leaveTypeList');
    },
    onModalOpen() {
      this.showModal = true;
    },
    onModalClose() {
      this.showModal = false;
    }
  }
};
