import { required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import { OxdSwitchInput } from '@ohrm/oxd';
import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@/core/util/services/api.service';
import useServerValidation from '@/core/util/composable/useServerValidation';
const initialClaimEvent = {
  name: '',
  description: '',
  status: true
};
export default {
  components: {
    'oxd-switch-input': OxdSwitchInput
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/claim/events');
    const {
      createUniqueValidator
    } = useServerValidation(http);
    const claimEventNameUniqueValidation = createUniqueValidator('ClaimEvent', 'name', {
      matchByField: 'isDeleted',
      matchByValue: 'false'
    });
    return {
      http,
      claimEventNameUniqueValidation
    };
  },
  data() {
    return {
      isLoading: false,
      claimEvent: {
        ...initialClaimEvent
      },
      rules: {
        name: [required, this.claimEventNameUniqueValidation, shouldNotExceedCharLength(100)],
        description: [shouldNotExceedCharLength(1000)]
      }
    };
  },
  methods: {
    onCancel() {
      navigate('/claim/viewEvents');
    },
    onSave() {
      this.isLoading = true;
      this.http.create({
        ...this.claimEvent,
        name: this.claimEvent.name.trim()
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        this.onCancel();
      });
    }
  }
};
