import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@ohrm/core/util/services/api.service';
import { required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import useServerValidation from '@/core/util/composable/useServerValidation';
export default {
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/licenses');
    const {
      createUniqueValidator
    } = useServerValidation(http);
    const licenseUniqueValidation = createUniqueValidator('License', 'name');
    return {
      http,
      licenseUniqueValidation
    };
  },
  data() {
    return {
      isLoading: false,
      license: {
        id: '',
        name: ''
      },
      rules: {
        name: [required, shouldNotExceedCharLength(100), this.licenseUniqueValidation]
      }
    };
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.create({
        name: this.license.name
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        this.license.name = '';
        this.onCancel();
      });
    },
    onCancel() {
      navigate('/admin/viewLicenses');
    }
  }
};
