import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@ohrm/core/util/services/api.service';
import { required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import useServerValidation from '@/core/util/composable/useServerValidation';
export default {
  props: {
    qualificationLanguageId: {
      type: Number,
      required: true
    }
  },
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/languages');
    const {
      createUniqueValidator
    } = useServerValidation(http);
    const langugageUniqueValidation = createUniqueValidator('Language', 'name', {
      entityId: props.qualificationLanguageId
    });
    return {
      http,
      langugageUniqueValidation
    };
  },
  data() {
    return {
      isLoading: false,
      language: {
        id: '',
        name: ''
      },
      rules: {
        name: [required, shouldNotExceedCharLength(120), this.langugageUniqueValidation]
      }
    };
  },
  created() {
    this.isLoading = true;
    this.http.get(this.qualificationLanguageId).then(response => {
      const {
        data
      } = response.data;
      this.language.id = data.id;
      this.language.name = data.name;
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.update(this.qualificationLanguageId, {
        name: this.language.name
      }).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    onCancel() {
      navigate('/admin/viewLanguages');
    }
  }
};
