import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@/core/util/services/api.service';
import { required, shouldNotExceedCharLength, validPhoneNumberFormat } from '@ohrm/core/util/validation/rules';
import useServerValidation from '@/core/util/composable/useServerValidation';
const initialLocation = {
  name: '',
  countryCode: null,
  province: '',
  city: '',
  address: '',
  zipCode: '',
  phone: '',
  fax: '',
  note: ''
};
export default {
  props: {
    locationId: {
      type: String,
      required: true
    },
    countries: {
      type: Array,
      default: () => []
    }
  },
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/locations');
    const {
      createUniqueValidator
    } = useServerValidation(http);
    const locationUniqueValidation = createUniqueValidator('Location', 'name', {
      entityId: props.locationId
    });
    return {
      http,
      locationUniqueValidation
    };
  },
  data() {
    return {
      isLoading: false,
      location: {
        ...initialLocation
      },
      rules: {
        name: [required, shouldNotExceedCharLength(100), this.locationUniqueValidation],
        countryCode: [required],
        province: [shouldNotExceedCharLength(50)],
        city: [shouldNotExceedCharLength(50)],
        address: [shouldNotExceedCharLength(250)],
        zipCode: [shouldNotExceedCharLength(30)],
        phone: [shouldNotExceedCharLength(30), validPhoneNumberFormat],
        fax: [shouldNotExceedCharLength(30), validPhoneNumberFormat],
        note: [shouldNotExceedCharLength(250)]
      }
    };
  },
  computed: {
    hasUpdatePermissions() {
      return this.$can.update(`locations`);
    }
  },
  created() {
    this.isLoading = true;
    this.http.get(parseInt(this.locationId, 10)).then(response => {
      const {
        data
      } = response.data;
      this.location.name = data.name;
      this.location.countryCode = this.countries.find(item => item.id === data.country.countryCode);
      this.location.province = data.province;
      this.location.city = data.city;
      this.location.address = data.address;
      this.location.zipCode = data.zipCode;
      this.location.phone = data.phone;
      this.location.fax = data.fax;
      this.location.note = data.note;
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onCancel() {
      navigate('/admin/viewLocations');
    },
    onSave() {
      this.isLoading = true;
      this.http.update(this.locationId, {
        name: this.location.name,
        countryCode: this.location.countryCode.id,
        province: this.location.province,
        city: this.location.city,
        address: this.location.address,
        zipCode: this.location.zipCode,
        phone: this.location.phone,
        fax: this.location.fax,
        note: this.location.note
      }).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => {
        this.onCancel();
      });
    }
  }
};
