import { OxdSwitchInput } from '@ohrm/oxd';
import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@/core/util/services/api.service';
import { required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import useServerValidation from '@/core/util/composable/useServerValidation';
const initialExpenseTypes = {
  name: '',
  description: '',
  status: false
};
export default {
  components: {
    'oxd-switch-input': OxdSwitchInput
  },
  props: {
    id: {
      type: Number,
      required: true
    }
  },
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/claim/expenses/types');
    const {
      createUniqueValidator
    } = useServerValidation(http);
    const expenseTypeNameUniqueValidation = createUniqueValidator('ExpenseType', 'name', {
      entityId: props.id,
      matchByField: 'isDeleted',
      matchByValue: 'false'
    });
    return {
      http,
      expenseTypeNameUniqueValidation
    };
  },
  data() {
    return {
      isLoading: false,
      expenseTypes: {
        ...initialExpenseTypes
      },
      canEdit: false,
      name: '',
      rules: {
        name: [required, this.expenseTypeNameUniqueValidation, shouldNotExceedCharLength(100)],
        description: [shouldNotExceedCharLength(1000)]
      }
    };
  },
  beforeMount() {
    this.isLoading = true;
    this.http.get(this.id).then(response => {
      const {
        data
      } = response.data;
      this.expenseTypes = {
        ...data
      };
      this.name = data.name;
      this.canEdit = response.data.meta.canEdit;
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onCancel() {
      navigate('/claim/viewExpense');
    },
    onSave() {
      this.isLoading = true;
      this.http.update(this.id, {
        name: this.canEdit ? this.expenseTypes.name : null,
        description: this.expenseTypes.description,
        status: this.expenseTypes.status
      }).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => {
        this.onCancel();
      });
    }
  }
};
