import LangStringTargetInput from '@/orangehrmAdminPlugin/components/LangStringTargetInput.vue';
export default {
  components: {
    LangStringTargetInput
  },
  props: {
    langstrings: {
      type: Array,
      required: true
    }
  },
  emits: ['update:langstrings'],
  setup(props, context) {
    const onUpdateTranslation = (value, index) => {
      context.emit('update:langstrings', props.langstrings.map((item, _index) => {
        let oldTarget = item.modified ? item.oldTarget : item.target ?? '';
        if (_index === index) {
          return {
            ...item,
            target: value,
            oldTarget: oldTarget,
            modified: true
          };
        }
        return item;
      }));
    };
    return {
      onUpdateTranslation
    };
  }
};
