import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@ohrm/core/util/services/api.service';
import { required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import useServerValidation from '@/core/util/composable/useServerValidation';
export default {
  props: {
    educationId: {
      type: Number,
      required: true
    }
  },
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/educations');
    const {
      createUniqueValidator
    } = useServerValidation(http);
    const educationUniqueValidation = createUniqueValidator('Education', 'name', {
      entityId: props.educationId
    });
    return {
      http,
      educationUniqueValidation
    };
  },
  data() {
    return {
      isLoading: false,
      qualification: {
        id: '',
        name: ''
      },
      rules: {
        name: [required, this.educationUniqueValidation, shouldNotExceedCharLength(100)]
      }
    };
  },
  created() {
    this.isLoading = true;
    this.http.get(this.educationId).then(response => {
      const {
        data
      } = response.data;
      this.qualification.id = data.id;
      this.qualification.name = data.name;
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.update(this.educationId, {
        name: this.qualification.name
      }).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    onCancel() {
      navigate('/admin/viewEducation');
    }
  }
};
