import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@ohrm/core/util/services/api.service';
import { required, validSelection, validTimeFormat, shouldNotExceedCharLength, endTimeShouldBeAfterStartTime } from '@ohrm/core/util/validation/rules';
import { diffInTime } from '@/core/util/helper/datefns';
import useServerValidation from '@/core/util/composable/useServerValidation';
import WorkShiftEmployeeAutocomplete from '@/orangehrmAdminPlugin/components/WorkShiftEmployeeAutocomplete';
const workShiftModel = {
  id: '',
  name: '',
  hoursPerDay: '',
  startTime: null,
  endTime: null,
  empNumbers: []
};
export default {
  components: {
    'work-shift-employee-autocomplete': WorkShiftEmployeeAutocomplete
  },
  props: {
    workShiftId: {
      type: Number,
      required: true,
      default: null
    }
  },
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/work-shifts');
    const {
      createUniqueValidator
    } = useServerValidation(http);
    const workShiftUniqueValidation = createUniqueValidator('WorkShift', 'name', {
      entityId: props.workShiftId
    });
    return {
      http,
      workShiftUniqueValidation
    };
  },
  data() {
    return {
      isLoading: false,
      workShift: {
        ...workShiftModel
      },
      rules: {
        name: [required, this.workShiftUniqueValidation, shouldNotExceedCharLength(50)],
        fromTime: [required, validTimeFormat],
        endTime: [required, validTimeFormat, endTimeShouldBeAfterStartTime(() => this.workShift.startTime, 'To time should be after from time')],
        empNumbers: [validSelection]
      }
    };
  },
  computed: {
    selectedTimeDuration() {
      return parseFloat(diffInTime(this.workShift.startTime, this.workShift.endTime) / 3600).toFixed(2);
    }
  },
  beforeMount() {
    this.isLoading = true;
    this.http.get(this.workShiftId).then(response => {
      const {
        data
      } = response.data;
      this.workShift.id = data.id;
      this.workShift.name = data.name;
      this.workShift.hoursPerDay = data.hoursPerDay;
      this.workShift.startTime = data.startTime;
      this.workShift.endTime = data.endTime;
      this.workShift.empNumbers = data.employees.map(employee => {
        return {
          id: employee.empNumber,
          label: `${employee.firstName} ${employee.middleName} ${employee.lastName}`,
          isPastEmployee: employee.terminationId ? true : false
        };
      });
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      const payload = {
        name: this.workShift.name,
        hoursPerDay: this.selectedTimeDuration,
        startTime: this.workShift.startTime,
        endTime: this.workShift.endTime,
        empNumbers: this.workShift.empNumbers.map(employee => employee.id)
      };
      this.http.update(this.workShiftId, payload).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    onCancel() {
      navigate('/admin/workShift');
    }
  }
};
